                                <Grid item xs={12} sm={6}>
                                    <div className="thumbnail-preview-wrapper img-thumbnail">

                                        {
                                            room.gif && room.gif !== "" ? (
                                                <img src={room.gif} alt={room.alt_text || ""} />
                                            ) : (
                                                <img src={require("./../../assets/img/placeholder.png")} alt=""
                                                />
                                            )
                                        }
                                    </div>
                                    <Fragment>
                                        <MaterialButton
                                            variant="outlined"
                                            color="primary"
                                            startIcon={<Image />}
                                            className="mt-1"
                                            fullWidth
                                            onClick={() => {
                                                setIsSingle(false);
                                                setIsAuthorImg(true);
                                                setShowGallery(true);
