import React, { Fragment, useEffect, useState } from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
// core components
// import GridItem from "components/Grid/GridItem.js";
// import GridContainer from "components/Grid/GridContainer.js";
// import CustomInput from "components/CustomInput/CustomInput.js";
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import MaterialButton from "@material-ui/core/Button";
import Button from "components/CustomButtons/Button.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardAvatar from "components/Card/CardAvatar.js";
import CardBody from "components/Card/CardBody.js";
import CardFooter from "components/Card/CardFooter.js";

import avatar from "assets/img/faces/marc.jpg";
import {
  MenuItem,
  Select,
  FormControl,
  TextField,
  CardMedia,
  CardActionArea,
  CardContent,
  CardActions,
  RadioGroup, FormControlLabel, Radio
} from "@material-ui/core";
import CKEditor from 'ckeditor4-react';
import { ckEditorConfig } from "utils/data";

// import { CKEditor } from '@ckeditor/ckeditor5-react';
// import ClassicEditor from '@arslanshahab/ckeditor5-build-classic';
import { Image } from "@material-ui/icons";
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { useParams } from "react-router-dom";
import API from "utils/http";
import LangAPI from "langapi/http";
import GalleryDialog from "../../../Common/GalleryDialog";
import { AddCircleOutline, DeleteOutlined } from "@material-ui/icons";

// const website_url = "/";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    // width:'60%',
    // margin:'auto'
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));


export default function AddHomePage() {
  const pageId = useParams().id;
  const classes = useStyles();

  let initObj = {
    banner: [
      {
        heading: '',
        sub_heading: "",
        image: ""
      },
    ],
    contact: [
      {
        icon: "",
        text: "",
        value: ""
      },
      {
        icon: "",
        text: "",
        value: ""
      },
      {
        icon: "",
        text: "",
        value: "",
        value2: "#",
      }
    ],
    collections: [
      {
        heading: "",
        sub_heading: "",
        starting_from: "",
        image: ""
      },
      {
        heading: "",
        sub_heading: "",
        starting_from: "",
        image: ""
      },
      {
        heading: "",
        sub_heading: "",
        starting_from: "",
        image: ""
      },
      {
        heading: "",
        sub_heading: "",
        starting_from: "",
        image: ""
      },
    ],
    projects: {
      heading: "",
      sub_heading: "",
      image: ""
    },
    deal: [
      {
        heading_image1: "",
        heading_image2: "",
        sub_heading_image1: "",
        sub_heading_image2: "",
        old_price: "",
        new_price: "",
        expires_in: "",
        slider_images:
        {
          main_image: "",
          sub_image: ""
        }
      }
    ],
    video: {
      heading: "",
      icon: "",
      link: ""
    }
  }

  let seoObj = {
    id: 0,
    post_id: pageId || 0,
    meta_title: '',
    meta_description: '',
    // route: website_url,
    schema_markup: '',
    is_followed: true,
    is_indexed: true,
    is_indexed_or_is_followed: '1,1',
  }
  const [termsUse, setTermsUse] = useState(initObj);
  const [seoInfo, setSeoInfo] = useState(seoObj)
  const [currentSection, setCurrentSection] = useState("")
  const [currentIndex, setCurrentIndex] = useState(0)
  const [imageType, setImageType] = useState("")
  const [pageData, setPageData] = useState({})

  const [imagesData, setImagesData] = useState([])
  // const [uploadsPreview, setUploadsPreview] = useState(null)
  // const [selectedImages, setSelectedImages] = useState([])
  const [showGallery, setShowGallery] = useState(false)
  const [isSingle, setIsSingle] = useState(true)
  // const [renderPreviews, setRenderPreviews] = useState(false)
  const [thumbnailPreview, setThumbnailPreview] = useState('')
  const [selectedLang, setSelectedLang] = useState("en");

  useEffect(() => {
    var splitUrl = window.location.href.split('/');
    let route = splitUrl[splitUrl.length - 1]
    LangAPI.get(`/pages/${route}?${selectedLang}`).then(response => {
      if (response?.status === 200) {
        setPageData(response.data);
        if (response.data.content) {
          console.log(response.data.content);
          let data = response.data.content;
          // data.banner = [
          //   {
          //     heading: "",
          //     sub_heading: "",
          //     image: ""
          //   }
          // ]
          setTermsUse(data)
          if (response.data.content.meta) {
            setSeoInfo(response.data.content.meta)
          }

        }

      }
    }).catch(err => console.log(err));;



    if (!imagesData.length > 0) {
      getGalleryImages();
    }
  }, [selectedLang]);

  const getGalleryImages = () => {
    LangAPI.get(`/uploads`).then((response) => {
      if (response.status === 200) {
        setImagesData(response.data?.data?.map((x) => ({ ...x, isChecked: false })));
      }
    }).catch(err => console.log(err));
  };

  const handleInputChange = (e, section, index = -1) => {
    let updatedAbout = { ...termsUse };
    if (index > -1) {
      updatedAbout[section][index][e.target.name] = e.target.value;
    } else {
      updatedAbout[section][e.target.name] = e.target.value;
    }
    setTermsUse(updatedAbout);
  }

  const handleImageSelect = (e, index, section) => {
    setTimeout(() => {
      setShowGallery(false);
    }, 500);

    if (e.target.checked) {
      if (section == "banner") {

        console.log("banner :: ",)
        let upTerm = { ...termsUse }
        // upTerm.banner = [
        //   {
        //     heading: "",
        //     sub_heading: "",
        //     image: ""
        //   }
        // ]
        upTerm[section][currentIndex - 1].image = imagesData[index].avatar
        setTermsUse(upTerm)
        setCurrentIndex(0)
        setTimeout(() => {
          setShowGallery(false);
