import React, { Fragment, useEffect, useState } from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import MaterialButton from "@material-ui/core/Button";
import Button from "components/CustomButtons/Button.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardAvatar from "components/Card/CardAvatar.js";
import CardBody from "components/Card/CardBody.js";
import CardFooter from "components/Card/CardFooter.js";
import LangAPI from "langapi/http";
import avatar from "assets/img/faces/marc.jpg";
import { FormControl, FormControlLabel, Radio, RadioGroup, Select, TextField, CardMedia, CardActionArea, CardContent, CardActions, MenuItem } from "@material-ui/core";
import CKEditor from 'ckeditor4-react';
import { ckEditorConfig } from "utils/data";
import { Image } from "@material-ui/icons";
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import { useParams } from "react-router-dom";
import API from "utils/http";
import GalleryDialog from "views/Common/GalleryDialog";

// const website_url = "https://fishermanscove-resort.com/";
// const website_url = "/";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    // width:'60%',
    // margin:'auto'
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));


export default function LeisureActivities() {
  const pageId = useParams().id;
  const classes = useStyles();
  let initObj = {
    banner: {
      id: 0,
      section_name: '',
      section_content: "<p>Detailed content goes here!</p>",
      page_id: pageId,
      section_avatar: '',
      section_col_arr: 0,
      section_prior: 1,
      section_avtar_alt: '',
      section_slug: 'banner'
    },
    awards: {
      id: 0,
      section_name: '',
      section_content: "<p>Detailed content goes here!</p>",
      page_id: pageId,
      section_avatar: '',
      section_col_arr: 0,
      section_prior: 1,
      section_avtar_alt: '',
      section_slug: 'awards'
    },
    excellence: {
      id: 0,
      section_name: '',
      section_content: "<p>Detailed content goes here!</p>",
      page_id: pageId,
      section_avatar: '',
      section_col_arr: 0,
      section_prior: 1,
      section_avtar_alt: '',
      section_slug: 'excellence'
    },
    activities: {
      id: 0,
      section_name: '',
      section_content: "<p>Detailed content goes here!</p>",
      page_id: pageId,
      section_avatar: '',
      section_col_arr: 0,
      section_prior: 1,
      section_avtar_alt: '',
      section_slug: 'activities'
    },
    Fishing: {
      id: 0,
      section_name: '',
      section_content: "<p>Detailed content goes here!</p>",
      page_id: pageId,
      section_avatar: '',
      section_col_arr: 0,
      section_prior: 1,
      section_avtar_alt: '',
      section_slug: 'Fishing'
    },
    Unlock: {
      id: 0,
      section_name: '',
      section_content: "<p>Detailed content goes here!</p>",
      page_id: pageId,
      section_avatar: '',
      section_col_arr: 0,
      section_prior: 1,
      section_avtar_alt: '',
      section_slug: 'Unlock'
    },
    Water: {
      id: 0,
      section_name: '',
      section_content: "<p>Detailed content goes here!</p>",
      page_id: pageId,
      section_avatar: '',
      section_col_arr: 0,
      section_prior: 1,
      section_avtar_alt: '',
      section_slug: 'Water'
    },
  }

  const [leisure, setLeisure] = useState(initObj)

  const [seoInfo, setSeoInfo] = useState({
    id: 0,
    post_id: pageId || 0,
    meta_title: '',
    meta_description: '',
    // route: website_url,
    schema_markup: '',
    is_followed: true,
    is_indexed: true,
    is_indexed_or_is_followed: '1,1',
  })

  const [currentSection, setCurrentSection] = useState("")

  const [imagesData, setImagesData] = useState([])
  // const [uploadsPreview, setUploadsPreview] = useState(null)
  // const [selectedImages, setSelectedImages] = useState([])
  const [showGallery, setShowGallery] = useState(false)
  const [isSingle, setIsSingle] = useState(true)
  // const [renderPreviews, setRenderPreviews] = useState(false)
  const [selectedLang, setSelectedLang] = useState("en");
  const [thumbnailPreview, setThumbnailPreview] = useState({
    banner: "",
    activities: "",
    awards: "",
    excellence: "",
    Fishing: "",
    Unlock: "",
    Water: "",
  })

  useEffect(() => {
    LangAPI.get(`/all-sections/${pageId}/${selectedLang}`).then(response => {
      if (response?.status === 200) {
        const { data } = response;
        console.log(data,"datadata for leasure act")
        // setLeisure(
        //     {
        //       banner: data.find(x => x.section_slug === "banner") || leisure.banner,
        //       activities: data.find(x => x.section_slug === "activities") || leisure.activities,
        //       awards: data.find(x => x.section_slug === "awards") || leisure.awards,
        //       excellence: data.find(x => x.section_slug === "excellence") || leisure.excellence,
        //       Fishing: data.find(x => x.section_slug === "Fishing") || leisure.Fishing,
        //       Unlock: data.find(x => x.section_slug === "Unlock") || leisure.Unlock,
        //       Water: data.find(x => x.section_slug === "Water") || leisure.Water,
        //     }
        // )
        if(response.data.data[0]){
          setLeisure(response.data.data[0])
          setSeoInfo(response?.data?.data[0]?.meta)
        } else {
          setSeoInfo({
            id: 0,
            post_id: pageId || 0,
            meta_title: '',
            meta_description: '',
            // route: website_url,
            schema_markup: '',
            is_followed: true,
            is_indexed: true,
            is_indexed_or_is_followed: '1,1',
          })
          setLeisure(initObj)
        }
      }
    });

    if(!imagesData.length > 0){
      getGalleryImages();
    }

    // getSEOInfo();
  }, [selectedLang])

  const getGalleryImages = () => {
    LangAPI.get(`/get_all_images`).then((response) => {
        if (response.status === 200) {
            setImagesData(response.data?.data?.map((x) => ({ ...x, isChecked: false })));
        }
    });
};

  const getSEOInfo = () => {
    API.get(`/meta/${pageId}`).then(response => {
      if (response.status === 200) {
        let seoInfoData = response.data;
        if (seoInfoData) {
          setSeoInfo(seoInfoData);
        }
        else {
          seoInfoData(seoInfo);
        }
      }
    })
  }

  const handleInputChange = (e, section) => {

    let updatedLeisureInner = { ...leisure };
    updatedLeisureInner[section][e.target.name] = e.target.value;
    setLeisure(updatedLeisureInner);
  }

  const handleImageSelect = (e, index, section) => {
    setTimeout(() => {
      setShowGallery(false);
    }, 500);

    if (e.target.checked) {
      // if (isSingle && thumbnailPreview !== "") {
      //   alert("You can only select 1 image for thubnail. If you want to change image, deselect the image and then select a new one");
      //   return;
      // } else {
      setLeisure({ ...leisure, [section]: { ...leisure[section], section_avatar: imagesData[index] } })
      // setThumbnailPreview(imagesData[index].avatar)
      setThumbnailPreview({...thumbnailPreview, [section]: imagesData[index].avatar })

      // let imagesDataUpdated = imagesData.map((x, i) => {
      //   if (i === index) {
      //     return {
      //       ...x,
      //       isChecked: true
      //     }
      //   } else {
      //     return x
      //   }
      // });
      // setImagesData(imagesDataUpdated);
      // }
    } else {
