      if (section == "projectReferencesImg") {
        let upTerm = { ...termsUse }
        upTerm.projectReferences[currentIndex - 1].image = imagesData[index].avatar
        setTermsUse(upTerm)
        setCurrentIndex(0)
        setTimeout(() => {
          setShowGallery(false);
        }, 500);

