  //   updatedSeoInfo[e.target.name] = website_url + updatedValue;
  //   setSeoInfo(updatedSeoInfo);
  // }

  const handleSEOSubmit = () => {
    let updatedSeoInfo = seoInfo;
    updatedSeoInfo.is_indexed_or_is_followed = `${updatedSeoInfo.is_indexed},${updatedSeoInfo.is_followed}`;

    if (updatedSeoInfo.id > 0) {
      API.put(`/meta/${pageId}`, updatedSeoInfo).then(response => {
        if (response.status === 200) {
          alert("Section updated successfully !");
        }
      }).catch(err => console.log(err))
    } else {
      API.post(`/meta`, updatedSeoInfo).then(response => {
        if (response.status === 200) {
          alert("Section updated successfully !");
        }
      }).catch(err => console.log(err))

    }
  }

  const handleSubmit = () => {
    // API.post(`/add_section`, about[name]).then(response => {
    //   if (response.status === 200) {
    //     alert("Section updated successfully !");
    //   }
    // }).catch(err => console.log(err))

    let updatedRoomsSuits = { ...about };
    updatedRoomsSuits.meta = {...seoInfo};
    updatedRoomsSuits.page_id = pageId
    updatedRoomsSuits.slug="roomsSuits-sections"
    // console.log("updatedRoomsSuits",updatedRoomsSuits); return false;

    LangAPI.post(`/add-section?lang=${selectedLang}`, updatedRoomsSuits).then(response => {
      if (response.status === 200) {
        alert("Section updated successfully !");
      }
    }).catch(err => console.log(err))

  }

  const handleChange = (event) => {
    // setAge(event.target.value as string);
    if (event.target.value != selectedLang) {
        setSelectedLang(event.target.value)
    }
  };

  return (
      <div>
        <div className={classes.root}>
          <Card>
            <CardHeader color="primary" className="d-flex justify-content-between align-items-center">
              <h4 className="mb-0">Add Rooms & Suites Sections</h4>
              {/* <p className={classes.cardCategoryWhite}>Complete your profile</p> */}
              <FormControl
                variant="outlined"
                size="small"
                style={{ width: "20%", color: "white" }}
            // fullWidth
            >
                <InputLabel id="language"
                    style={{ color: "white" }}
                >Select Language</InputLabel>
                <Select
                    labelId="language"
                    id="language"
                    name="language"
                    value={selectedLang}
                    label="Select Language"
                    fullWidth
                    style={{ color: "white" }}
                    onChange={handleChange}
                >
                    <MenuItem value={'en'}>En</MenuItem>
                    <MenuItem value={'fr'}>FR</MenuItem>
                    <MenuItem value={'de'}>DE</MenuItem>

                </Select>
            </FormControl>
            </CardHeader>
            <CardBody>
              {/* ******************* */}
              {/* SECTION BANNER */}
              {/* ******************* */}
              <Accordion>
                <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls="panelaa-content"
                    id="panelaa-header"
                >
                  <Typography className={classes.heading}>Banner</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <Grid container spacing={2}>
                    <Grid item xs={12} sm={12}>
                      {/* SECTION TITLE */}
                      <TextField
                          required
                          id="section_name"
                          name="section_name"
                          label="Section Title"
                          value={about.banner.section_name}
                          variant="outlined"
                          fullWidth
                          onChange={(e) => handleInputChange(e, "banner")}
                          size="medium"
                          style={{ marginBottom: '1rem' }}
                      />

                      <div className="thumbnail-preview-wrapper-large img-thumbnail">
                        {
                          !about.banner.id > 0 ?
                            about.banner.section_avatar?.avatar !== "" ?
                                  <img src={about.banner.section_avatar?.avatar} alt={about.banner.section_avtar_alt || ""} />
                                  :
                                  <img src="https://cpworldgroup.com/wp-content/uploads/2021/01/placeholder.png" alt="" />
                              :
                              typeof (about.banner.section_avatar?.avatar) === typeof (0) ?
                                  // dining.thumbnail && dining.thumbnail !== "" ?
                                  <img src={thumbnailPreview} alt={about.banner.section_avtar_alt || ""} />
                                  :
                                  <img src={about.banner.section_avatar?.avatar} alt={about.banner.section_avtar_alt || ""} />
                        }
                      </div>
                      <Fragment>
                        <MaterialButton
                            variant="outlined"
                            color="primary"
                            startIcon={<Image />}
                            className="mt-1"
                            fullWidth
                            size="large"
                            onClick={() => {
                              setIsSingle(true);
                              setCurrentSection("banner");
                              setShowGallery(true);
                            }}
                        >
                          Upload Featured Image
                        </MaterialButton>
                      </Fragment>
                    </Grid>
                  </Grid>
                </AccordionDetails>
              </Accordion>
              {/* ******************* */}
              {/* SECTION 1 */}
              {/* ******************* */}
              <Accordion>
                <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls="panel1a-content"
                    id="panel1a-header"
                >
                  <Typography className={classes.heading}>Intro</Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <Grid container spacing={2}>
                    <Grid item xs={12} sm={12}>
                      {/* SECTION TITLE */}
                      <TextField
                          required
                          id="section_name"
                          name="section_name"
                          label="Section Title"
                          value={about.intro.section_name}
                          variant="outlined"
                          fullWidth
                          onChange={(e) => handleInputChange(e, "intro")}
                          size="small"
                          style={{ marginBottom: '1rem' }}
                      />
                      {/* CKEDITOR  */}
                      <CKEditor
                          config={ckEditorConfig}
                          onBeforeLoad={(CKEDITOR) => (CKEDITOR.disableAutoInline = true)} data={about.intro.section_content} onChange={(e) => setAbout({ ...about, intro: { ...about.intro, section_content: e.editor.getData() } })} />
                    </Grid>
                  </Grid>
                </AccordionDetails>
              </Accordion>
              {/* ****************** */}
              {/* SECTION 2 */}
              {/* ****************** */}
              {/*<Accordion>*/}
              {/*  <AccordionSummary*/}
              {/*      expandIcon={<ExpandMoreIcon />}*/}
