export declare function toObject<T extends string | number>(array: T[]): {
    [key: string]: T;
};
export declare function toObject<T extends string | number, K>(array: T[], transformer: (item: T) => K): {
    [key: string]: K;
};
export declare function toObject<T, P extends keyof T>(array: T[], key: P): {
    [key: string]: T;
};
export declare function toObject<T, P extends keyof T, K>(array: T[], key: P, transformer: (item: T) => K): {
    [key: string]: K;
};
